// Package router implements a simple HTTP request routerpackage routerimport ("net/http")type HandlerFn func(http.ResponseWriter, *http.Request)type Router struct {paths map[string]HandlerFn}// New creates a new Routerfunc New() *Router {return &Router{paths: make(map[string]HandlerFn),}}// Register registers a new route with a handlerfunc (r *Router) Register(path string, handler HandlerFn) {r.paths[path] = handler}// ServeHTTP implements http.Handlerfunc (r *Router) ServeHTTP(w http.ResponseWriter, req *http.Request) {if handler, ok := r.paths[req.URL.Path]; ok {handler(w, req)} else {w.WriteHeader(http.StatusNotFound)w.Write([]byte("404 - Not Found"))}}